/* eslint-disable vue/no-unused-components */
import { IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonIcon, IonCardContent, IonList, IonItem, modalController, IonModal, IonHeader, IonToolbar, IonButtons, IonButton, IonSearchbar } from '@ionic/vue';
import ChatItem from "@/components/ChatItem.vue";
export default {
  name: "ChatSpace",
  setup() {
    return {
      groupId: 'yoyoyo'
    };
  },
  components: {
    ChatItem,
    IonCard,
    IonCardHeader,
    IonCardSubtitle,
    IonCardTitle,
    IonIcon,
    IonCardContent,
    IonList,
    IonItem,
    modalController,
    IonModal,
    IonHeader,
    IonToolbar,
    IonButtons,
    IonButton,
    IonSearchbar
  },
  props: {},
  methods: {
    async openModal() {
      const modal = await modalController.create({
        component: IonModal,
        componentProps: {
          groupId: this.groupId
        }
      });
      return await modal.present();
    }
  }
};
