import { defineStore } from 'pinia';
import sdk, {EventType} from 'matrix-js-sdk';

const client = sdk.createClient({
  baseUrl: "https://matrix.org",
  accessToken: "syt_b2JzZXJ2ZXJ6ZXJv_qbsSNMdGRZpXMsNvXJeX_3OODDh",
  userId: "@observerzero:matrix.org"
});

client.startClient();

function getAllRooms() {
  const rooms = client.getRooms();
  console.log(rooms);
  return rooms;
}

async function getARoom(roomId) {
  const room = await client.getRoom(roomId);
  return room;
}

function getAllRoomNames() {
  const rooms = getAllRooms();
  const roomNames = rooms.map(room => room.name);
  //roomNames.forEach(roomName => console.log(roomName))
  return roomNames;
}

function getAllRoomIds() {
  const rooms = getAllRooms();
  const roomIds = rooms.map(room => room.roomId);
  //roomIds.forEach(roomName => console.log(roomName))
  return roomIds;
}

function login() {
  client.loginWithPassword('username', 'password').then(function() {
    console.log('Logged in!')
  })
}

//TODO - Add a setter for creating a room
//TODO - Refactor for returning objects instead of arrays

export const useMatrixClient = defineStore('matrix-client', {
  state: () => {
    return ({
      client: client,
      getAllRooms: getAllRooms,
      getARoom: getARoom,
      getAllRoomNames: getAllRoomNames,
      getAllRoomIds: getAllRoomIds,
    });
  },
  getters: {
    getRoom: (state) => (roomId) => {
      return state.getARoom(roomId);
    },
    getRooms: (state) => () => {
      return state.getAllRooms();
    },
    getRoomNames: (state) => () => {
      return state.getAllRoomNames();
    },
    getRoomIds: (state) => () => {
      return state.getAllRoomIds();
    },
  }
})
