import { defineStore } from 'pinia';
import sdk, {EventType} from 'matrix-js-sdk';

let roomNamesAndIdsAsObject = {};
const accessToken = "syt_b2JzZXJ2ZXJ6ZXJv_qbsSNMdGRZpXMsNvXJeX_3OODDh"
const userId = "@observerzero:matrix.org";
const baseUrl = 'https://matrix.org'

const client = sdk.createClient({
  baseUrl: baseUrl,
  accessToken: accessToken,
  userId: userId,
});

client.startClient();

function getRoomTopic(roomId) {
  let room = client.getRoom(roomId);
  let topic = room.currentState.getStateEvents('m.room.topic', '');
  return topic.event.content;
}

