import { IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonIcon, IonCardContent, IonList, IonItem, modalController, IonModal } from '@ionic/vue';
import ChatItem from "@/components/ChatItem.vue";
export default {
  name: "ChatSpace",
  setup() {
    return {};
  },
  components: {
    IonCardSubtitle,
    IonIcon,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardContent,
    IonItem,
    IonList,
    ChatItem,
    IonModal
  },
  props: {},
  methods: {
    async openModal() {
      const modal = await modalController.create({
        component: IonModal,
        componentProps: {
          groupId: this.groupId
        }
      });
      return await modal.present();
    }
  }
};
