function returnRoomNamesFromRoomObject(rooms) {
  const roomNames = rooms.map(room => room.name)
  return roomNames
}

function returnRoomIdsFromRoomObject(rooms) {
  const roomIds = rooms.map(room => room.roomId)
  return roomIds
