function produceHrefAddressesForGroups(rooms) {
  const roomIds = rooms.map(room => {
    let id = room.roomId.replace(/:.*/, '')
    id = id.replace(/!/, '')
    id = "/tabs/group/" + id
    return id
  })
  // roomIds.forEach(roomId => console.log(roomId))
  return roomIds
