  let roomTypes = roomIds.map(roomId => getRoomType(roomId))
  let roomProduct = []
  for (let i = 0; i < roomNames.length; i++) {
    roomProduct.push({
      name: roomNames[i],
      id: roomIds[i],
      topic: roomTopics[i],
      type: roomTypes[i],
      handle: roomHandles[i],
      href: hrefs[i],
    })
