import { defineStore } from 'pinia';
import sdk, {EventType} from 'matrix-js-sdk';

let roomNamesAndIdsAsObject = {};

const client = sdk.createClient({
  baseUrl: "https://matrix.org",
  accessToken: "syt_b2JzZXJ2ZXJ6ZXJv_qbsSNMdGRZpXMsNvXJeX_3OODDh",
  userId: "@observerzero:matrix.org"
});

client.startClient();

function login() {
  client.loginWithPassword('username', 'password').then(function() {
    console.log('Logged in!')
  })
}

function getRooms() {
  const rooms = client.getRooms();
  console.log(rooms);
  return rooms;
}

function getAllRoomNames() {
  const rooms = getRooms();
  const roomNames = rooms.map(room => room.name);
  return roomNames;
}

function getAllRoomIds() {
  const rooms = getRooms();
  const roomIds = rooms.map(room => room.roomId);
  return roomIds;
}

function getAllRoomNamesAndIds() {
  let roomNames = getAllRoomNames();
  let roomIds = getAllRoomIds();
  let roomNamesAndIds = [];
  for (let i = 0; i < roomNames.length; i++) {
    roomNamesAndIds.push({name: roomNames[i], id: roomIds[i]})
  }
  Object.assign(roomNamesAndIdsAsObject, roomNamesAndIds)
  console.log(roomIds)
  return roomNamesAndIdsAsObject;
}

function returnRoomNamesFromRoomObject(rooms) {
  const roomNames = rooms.map(room => room.name);
  return roomNames;
}

function returnRoomIdsFromRoomObject(rooms) {
  const roomIds = rooms.map(room => room.roomId);
  return roomIds;
}

function getGroups() {
  let rooms = client.getRooms();
  let roomNames = returnRoomNamesFromRoomObject(rooms);
  let roomIds = returnRoomIdsFromRoomObject(rooms);
  let roomNamesAndIds = [];
  for (let i = 0; i < roomNames.length; i++) {
    roomNamesAndIds.push({name: roomNames[i], id: roomIds[i]})
  }
  Object.assign(roomNamesAndIdsAsObject, roomNamesAndIds)
  console.log(roomNamesAndIdsAsObject);
  return roomNamesAndIdsAsObject;
}

function createRoom() {
  client.createRoom({
    name: String,
    visibility: 'private',
  }).then((response) => {
    console.log(`Room created: ${response.room_id}`)
  });
}

//TODO - Add a setter for creating a room
//TODO - Refactor for returning objects instead of arrays
//TODO - Add a function to get a room's timeline
//TODO - Add a function to get a room's type

export const useMatrixClient = defineStore('matrix-client', {
  state: () => {
    return ({
      client: client,
      getRooms: getRooms,
      getAllRoomNames: getAllRoomNames,
      getAllRoomIds: getAllRoomIds,
      getAllRoomNamesAndIds: getAllRoomNamesAndIds,
      createRoom: createRoom,
    });
  },
  getters: {
    getRooms: (state) => () => {
      return state.getRooms();
    },
    getRoomNames: (state) => () => {
      return state.getAllRoomNames();
    },
    getRoomIds: (state) => () => {
      return state.getAllRoomIds();
    },
    getRoomNamesAndIds: (state) => () => {
      return state.getAllRoomNamesAndIds();
    },
  },
  setters: {
    createRoom: (state) => () => {
      return state.createRoom();
    }
  }
})
