import {modalController} from "@ionic/vue";
import Modal from "@/menus/Modal.vue";
import {defineStore} from "pinia";

function openModal() {
  doModal()
}

async function doModal() {
  const modal = await modalController.create({
    component: Modal,
  });
  await modal.present();

  const {data, role} = await modal.onWillDismiss();

  if (role === 'confirm') {
    console.log("sure thing")
  }
}

function wow() {
  console.log("wow")
}

let modalData = {}

export const useCrossPage = defineStore('crossPage', {
  state: () => {
    return ({
      wow,
      modalData,
    });
  },
  getters: {
    getModalData: (state) => () => {
      return state.modalData
    }
  },
  setters: {
    storeModalData: (state) => (data) => {
      state.modalData = data
    }
  },
})
