export default defineComponent({
  name: 'CreateSubGroupModal',
  components: {
    IonContent,
    IonHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonItem,
    IonLabel,
    IonInput,
    IonDatetime
  },
  data() {
    return {
      name: '',
      topic: ''
    };
  },
  props: {
    message: String
  },
  methods: {
    cancel() {
